CREATE TABLE `BPbuenasprac` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nombre` text COMMENT 'resumen de la Buena práctica',
  `descripcion` text COMMENT 'desarrollo de la buena práctica',
  `fuente` varchar(450) DEFAULT NULL COMMENT 'fuente. Docuemtno donde se encuentra publicada la buena práctica',
  `entidad` varchar(450) DEFAULT NULL,
  `fecha` date DEFAULT NULL,
  `id_p_FUfuentes` int(11) DEFAULT NULL COMMENT 'fuente registrada en nuestra base de fuentes',
  `copia` text COMMENT 'transcripción del texto de la fuente donde se incluye la buena práctica',
  `observaciones` text COMMENT 'alcances, limiteciones y observaciones de la fuente',
  `fuentepags` varchar(145) DEFAULT NULL COMMENT 'paginas de las que se ecxtrae la BP separadas por coma. Ej 6,7,8,13. refiere a las páginas 6, 7, 8 y 13 del documento de referencia',
  `valoracion` enum('rechazado','viable') DEFAULT NULL COMMENT 'valoración de la BUena práctica',
  `id_p_PROestrategias` int(11) DEFAULT NULL,
  `procedimiento` text,
  `recursos` text,
  `recorte` text COMMENT 'recorte destacable (opcional)',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=34 DEFAULT CHARSET=latin1;
